def invoke(msg){
        def key="";
        def value="";
	def oldhttpHeaders = msg.get("OrigReqHeaders");
	def currentHeaders = msg.get("http.headers");
	def oldheaderNames = oldhttpHeaders.getNames();
	while  (oldheaderNames.hasNext()){
		key = oldheaderNames.next();
		value = oldhttpHeaders.get(key);
		currentHeaders.setHeader(key,value);
	}
	currentHeaders.remove("ASE-Token");
        currentHeaders.remove("Server");
	return true;
}

