def invoke(msg)        
{        
    def content = msg.get("content.body");
    if (content != null) {
       msg.put("content.length",content.getContentLength(0));
    }    
    return true;         
}

def invoke(msg){
	def httpHeaders = msg.get("http.headers");
	if(msg.get("content.type") != null){
		httpHeaders.setHeader("Content-type",msg.get("content.type"));
	}
	if(msg.get("content.connection") != null){
		httpHeaders.setHeader("Connection", msg.get("http.connection"));
	}
	if(msg.get("content.transferencoding") != null){
		httpHeaders.setHeader("Transfer-Encoding", msg.get("http.transferencoding"));
	}

	msg.put("OrigReqHeaders", httpHeaders);
	def headerNames = httpHeaders.getNames();     
	def strHeaders = '{"source_ip": "'+ msg.get("sourceIP") + '", "source_port": "'+  msg.get("message.source")+'", "method": "' +msg.get("http.request.verb")+ '", "url": "' +msg.get("http.request.uri")+'",';
	strHeaders = strHeaders+'"headers": [';
	while (headerNames.hasNext()){
		def key = headerNames.next();
		def value = httpHeaders.get(key);
		strHeaders = strHeaders + '{"' + key + '" : "' + value +'"}' ;
		if(headerNames.hasNext()){
    			strHeaders = strHeaders + ","; 
		}
	}
	
	if(msg.get("content.length") != null){
		strHeaders = strHeaders+"," + '{"Content-Length" : "' + msg.get("content.length") +'"}' ;
	}	
	
	strHeaders = strHeaders + ']}';
	msg.put("ASEPostBody", strHeaders);
       return true;
}

def invoke(msg){
        def key="";
        def value="";
	def httpHeaders = msg.get("http.headers");
	if(msg.get("content.type") != null){
		httpHeaders.setHeader("Content-type",msg.get("content.type"));
	}
	if(msg.get("content.connection") != null){
		httpHeaders.setHeader("Connection", msg.get("http.connection"));
	}
	if(msg.get("content.transferencoding") != null){
		httpHeaders.setHeader("Transfer-Encoding", msg.get("http.transferencoding"));
	}
	msg.put("OrigResHeaders", httpHeaders);
	def headerNames = httpHeaders.getNames();     
	def strHeaders = '{ "response_code": "'+msg.get("http.response.status") + '", "response_status": "'+ msg.get("http.response.info") + '", "http_version": "' +msg.get("http.response.version")+'", ';
	msg.put("BackendResponseHeaders", msg.get("http.headers"));
	msg.put("BackendResponseCode", msg.get("http.response.status"));
	strHeaders = strHeaders + '"headers": [';
	while  (headerNames.hasNext()){
		key = headerNames.next().replaceAll("\"", "\\\\\"");
		value = httpHeaders.get(key).replaceAll("\"", "\\\\\"");
		strHeaders = strHeaders + '{"' + key + '" : "' + value +'"}' ;
		if(headerNames.hasNext()){
    			strHeaders = strHeaders + ","; 
		}
	}
	
	if(msg.get("content.length") != null) {
		strHeaders = strHeaders+"," + '{"Content-Length" : "' + msg.get("content.length") +'"}' ;
	}
	
	strHeaders = strHeaders + '] }';
	msg.put("ASEResponse", strHeaders);
       return true;
}

